import capitalize from "../utils/capitalize";
import currencyPtBr from "../utils/currencyPtBr";

class Card extends HTMLElement {
  constructor() {
    super();
    this.shadow = this.attachShadow({mode: 'open'});
  }

  connectedCallback() {
    this.data = JSON.parse(this.getAttribute('data'));
    this.render();
  }

  render() {    
    this.shadow.innerHTML = `
      <style>
        .card-container {
          background: #FFFFFF;
          box-shadow: 0px 28px 38px rgba(0, 0, 0, 0.0925754);
          border-radius: 5px;
          width: 250px;
          height: 320px;
          overflow: hidden;
          padding: 18px;
          text-align: center;
        }
        .card-container img {
          width: 187px;
          height: 221px;
        }
        .card-details {
          display: flex;
          align-items: center;
          justify-content: space-between;
        }

        .staff-favorite {
          background: #75BC97;
          border-radius: 0 10px 10px 0;
          width: 130px;
          padding: 6px 12px;
          margin-left: -18px;
        }
        .staff-favorite strong {
          color: #FFFFFF;
          font-size: 0.8rem;
        }
        .hidden {
          display: none;
        }
      </style>

      <div class="card-container"></div>
    `

    const card = this.shadow.querySelector('.card-container');
    console.log(location.pathname);

    card.innerHTML = `
      <div class="staff-favorite ${!this.data.staff_favorite && 'hidden'}"><strong>âœ¨ Staff favorite</strong></div>
      <img src="${this.data.url}" alt="${this.data.name}" class="image" />
      <div class="card-details">
        <h3>${capitalize(this.data.name)}</h3>
        <div>
          <span>${currencyPtBr(this.data.price)}</span>
          <span>${this.data.water && `<img src="/images/icons/water-${this.data.water}.svg" alt="water" />`}</span>
        </div>
      </div>
    `;
  }
}

customElements.define('card-component', Card);
